section.programlisting:
for (
	fs::directory_iterator itr( dir_path );
	itr != end_itr;
	++itr
    )
{
	if ( fs::is_directory( *itr ) )
	{
		find_matching_files ( *itr, match_func, result );
	}
	else if (match_func(itr->leaf()))
	{
		result.push_back(*itr);
	}
}

